import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useForm } from 'react-hook-form';
import { Eye, EyeOff, GraduationCap, Users } from 'lucide-react';
import { useAuth } from '../../contexts/AuthContext';
import LoadingSpinner from '../common/LoadingSpinner';
import HarmonyLogo from '../common/HarmonyLogo';
import { ThemeToggle, useTheme, ThemedLayout } from '../common/ThemeProvider';
import { SchoolHeader, AchievementBadge } from '../common/BrandingElements';

const Login = () => {
  const [userType, setUserType] = useState('student');
  const [showPassword, setShowPassword] = useState(false);
  const [loading, setLoading] = useState(false);
  const { login } = useAuth();
  const { theme } = useTheme();
  const navigate = useNavigate();

  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm();

  const onSubmit = async (data) => {
    setLoading(true);
    try {
      const result = await login(data, userType);
      if (result.success) {
        navigate('/dashboard');
      }
    } catch (error) {
      console.error('Login error:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <ThemedLayout>
      <div className="min-h-screen flex items-center justify-center hero-harmony py-4 px-4 sm:py-12 sm:px-6 lg:px-8 relative">
        {/* Background Pattern */}
        <div className="absolute inset-0 overflow-hidden">
          <div className="absolute -top-40 -right-40 w-80 h-80 bg-white bg-opacity-10 rounded-full"></div>
          <div className="absolute -bottom-40 -left-40 w-80 h-80 bg-white bg-opacity-10 rounded-full"></div>
          <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-96 h-96 bg-white bg-opacity-5 rounded-full"></div>
        </div>
        
        {/* Theme Toggle */}
        <div className="absolute top-4 right-4">
          <ThemeToggle />
        </div>
        
        <div className="max-w-md w-full space-y-6 sm:space-y-8 relative z-10">
          {/* Enhanced Header */}
          <div className="text-center">
            <div className="mx-auto mb-6">
              <HarmonyLogo size={80} showText={true} theme="white" />
            </div>
            <div className="space-y-2">
              <h2 className="text-2xl sm:text-3xl font-brand font-bold text-white">
                Welcome Back
              </h2>
              <p className="text-white text-opacity-90 text-sm">
                Sign in to continue your learning journey
              </p>
            </div>
          </div>
        {/* Enhanced Login Form */}
        <form className="mt-6 sm:mt-8 space-y-4 sm:space-y-6 card-harmony bg-white bg-opacity-95 backdrop-blur-sm" onSubmit={handleSubmit(onSubmit)}>
          {/* User Type Selection */}
          <div className="flex rounded-md shadow-sm" role="group">
            <button
              type="button"
              onClick={() => setUserType('student')}
              className={`px-3 sm:px-4 py-3 sm:py-2 text-sm font-medium rounded-l-lg border flex items-center space-x-2 flex-1 justify-center transition-all duration-300 min-h-12 touch-manipulation ${
                userType === 'student'
                  ? 'bg-gradient-secondary text-white border-harmony-secondary shadow-harmony'
                  : 'bg-white text-gray-700 border-gray-300 hover:bg-gray-50'
              }`}
            >
              <GraduationCap className="h-4 w-4" />
              <span>Student</span>
            </button>
            <button
              type="button"
              onClick={() => setUserType('staff')}
              className={`px-3 sm:px-4 py-3 sm:py-2 text-sm font-medium rounded-r-lg border flex items-center space-x-2 flex-1 justify-center transition-all duration-300 min-h-12 touch-manipulation ${
                userType === 'staff'
                  ? 'bg-gradient-primary text-white border-harmony-primary shadow-harmony'
                  : 'bg-white text-gray-700 border-gray-300 hover:bg-gray-50'
              }`}
            >
              <Users className="h-4 w-4" />
              <span>Staff</span>
            </button>
          </div>

          <div className="space-y-4">
            {/* Username/Email Field */}
            <div>
              <label htmlFor="username" className="block text-sm font-medium text-harmony-primary">
                {userType === 'student' ? 'Student Number' : 'Email Address'}
              </label>
              <input
                id="username"
                name="username"
                type={userType === 'student' ? 'text' : 'email'}
                autoComplete={userType === 'student' ? 'username' : 'email'}
                required
                {...register(userType === 'student' ? 'student_number' : 'email', {
                  required: `${userType === 'student' ? 'Student number' : 'Email'} is required`,
                })}
                className={`mt-1 appearance-none relative block w-full px-4 py-3 border ${
                  errors[userType === 'student' ? 'student_number' : 'email'] ? 'border-red-300' : 'border-gray-300'
                } placeholder-gray-500 text-gray-900 rounded-lg focus:outline-none focus:ring-2 focus:ring-harmony-primary focus:border-harmony-primary text-base min-h-12 transition-all duration-300`}
                placeholder={userType === 'student' ? 'Enter your student number' : 'Enter your email address'}
              />
              {errors[userType === 'student' ? 'student_number' : 'email'] && (
                <p className="mt-1 text-sm text-red-600">
                  {errors[userType === 'student' ? 'student_number' : 'email']?.message}
                </p>
              )}
            </div>

            {/* Password Field */}
            <div>
              <label htmlFor="password" className="block text-sm font-medium text-harmony-primary">
                Password
              </label>
              <div className="mt-1 relative">
                <input
                  id="password"
                  name="password"
                  type={showPassword ? 'text' : 'password'}
                  autoComplete="current-password"
                  required
                  {...register('password', {
                    required: 'Password is required',
                    minLength: {
                      value: 6,
                      message: 'Password must be at least 6 characters',
                    },
                  })}
                  className={`appearance-none relative block w-full px-4 py-3 pr-12 border ${
                    errors.password ? 'border-red-300' : 'border-gray-300'
                  } placeholder-gray-500 text-gray-900 rounded-lg focus:outline-none focus:ring-2 focus:ring-harmony-primary focus:border-harmony-primary text-base min-h-12 transition-all duration-300`}
                  placeholder="Enter your password"
                />
                <button
                  type="button"
                  className="absolute inset-y-0 right-0 pr-3 flex items-center touch-manipulation min-h-12 min-w-12 text-harmony-primary hover:text-harmony-secondary transition-colors duration-200"
                  onClick={() => setShowPassword(!showPassword)}
                  aria-label={showPassword ? 'Hide password' : 'Show password'}
                >
                  {showPassword ? (
                    <EyeOff className="h-5 w-5" />
                  ) : (
                    <Eye className="h-5 w-5" />
                  )}
                </button>
              </div>
              {errors.password && (
                <p className="mt-1 text-sm text-red-600">{errors.password.message}</p>
              )}
            </div>
          </div>

          <div>
            <button
              type="submit"
              disabled={loading}
              className={`btn-harmony-${userType === 'student' ? 'secondary' : 'primary'} w-full py-3 px-4 text-base font-medium min-h-12 touch-manipulation disabled:opacity-50 disabled:cursor-not-allowed`}
            >
              {loading ? (
                <LoadingSpinner size="sm" className="mr-2" />
              ) : null}
              {loading ? 'Signing in...' : 'Sign in'}
            </button>
          </div>

          {/* Demo credentials info */}
          <div className="bg-harmony-primary bg-opacity-5 rounded-lg p-4 mt-6 border border-harmony-primary border-opacity-20">
            <h3 className="text-sm font-medium text-harmony-primary mb-2">Demo Credentials</h3>
            <div className="text-xs text-gray-600 space-y-1">
              <p><strong>Super Admin:</strong> admin@harmonylearning.edu / admin123</p>
              <p><strong>Student:</strong> Use your student number for both username and password</p>
            </div>
          </div>
        </form>

          {/* Enhanced Footer */}
          <div className="text-center">
            <div className="card-harmony bg-white bg-opacity-95 backdrop-blur-sm">
              <div className="flex items-center justify-center space-x-6">
                <AchievementBadge type="excellence" />
                <AchievementBadge type="innovation" />
                <AchievementBadge type="academic" />
              </div>
              <p className="text-xs text-gray-500 mb-2 mt-4">Powered by</p>
              <div className="flex items-center justify-center space-x-2">
                <div className="h-8 w-8 bg-gradient-to-r from-purple-500 to-pink-500 rounded-full flex items-center justify-center">
                  <span className="text-white font-bold text-sm">A8</span>
                </div>
                <span className="text-sm font-medium text-gray-700">AutoM8</span>
              </div>
            </div>
          </div>
        </div>
      </div>
    </ThemedLayout>
  );
};

export default Login;
