import React, { useState } from 'react';
import { Outlet, useNavigate } from 'react-router-dom';
import { 
  Home, 
  BookOpen, 
  MessageSquare, 
  User, 
  Settings, 
  LogOut,
  Menu,
  X,
  GraduationCap,
  Users,
  BarChart3,
  FileText,
  Award,
  Calendar,
  Bell
} from 'lucide-react';
import { useAuth } from '../../contexts/AuthContext';
import HarmonyLogo from '../common/HarmonyLogo';
import { ThemeToggle, useTheme, ThemedLayout } from '../common/ThemeProvider';
import { StatusIndicator, DepartmentBadge, AchievementBadge } from '../common/BrandingElements';

const Layout = () => {
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const { user, logout, isAdmin } = useAuth();
  const { theme } = useTheme();
  const navigate = useNavigate();

  const handleLogout = async () => {
    await logout();
    navigate('/login');
  };

  const navigation = [
    { name: 'Dashboard', href: '/dashboard', icon: Home, color: 'harmony-primary' },
    { name: 'Tasks', href: '/tasks', icon: BookOpen, color: 'harmony-accent' },
    { name: 'Quizzes', href: '/quizzes', icon: Award, color: 'harmony-gold' },
    { name: 'Announcements', href: '/announcements', icon: MessageSquare, color: 'harmony-secondary' },
    { name: 'Documents', href: '/documents', icon: FileText, color: 'harmony-primary' },
    { name: 'Calendar', href: '/calendar', icon: Calendar, color: 'harmony-accent' },
    { name: 'Profile', href: '/profile', icon: User, color: 'harmony-primary' },
    ...(isAdmin ? [
      { name: 'Admin Panel', href: '/admin', icon: Settings, color: 'harmony-secondary' },
      { name: 'Users', href: '/users', icon: Users, color: 'harmony-accent' },
      { name: 'Analytics', href: '/analytics', icon: BarChart3, color: 'harmony-gold' }
    ] : []),
  ];

  const NavItem = ({ item, onClick }) => (
    <button
      onClick={() => {
        navigate(item.href);
        if (onClick) onClick();
      }}
      className={`group flex items-center px-3 py-3 text-base font-medium rounded-lg w-full text-left transition-all duration-300 min-h-12 touch-manipulation
        ${theme === 'dark' 
          ? 'text-gray-300 hover:bg-gray-700 hover:text-white' 
          : 'text-gray-600 hover:bg-gray-50 hover:text-gray-900'
        }
        hover:scale-105 hover:shadow-md
      `}
    >
      <item.icon className={`mr-4 h-6 w-6 flex-shrink-0 text-${item.color}`} />
      <span className="truncate">{item.name}</span>
    </button>
  );

  const getUserStatus = () => {
    if (user?.role === 'admin') return 'Administrator';
    if (user?.role === 'teacher') return 'Teacher';
    if (user?.role === 'student') return `Grade ${user?.grade || 'N/A'}`;
    return 'User';
  };

  const getUserBadge = () => {
    if (user?.role === 'admin') return <StatusIndicator status="active" label="Admin" />;
    if (user?.role === 'teacher') return <DepartmentBadge department={user?.department || 'General'} />;
    if (user?.role === 'student') return <StatusIndicator status="active" label={`Grade ${user?.grade || 'N/A'}`} />;
    return null;
  };

  return (
    <div className="h-screen flex overflow-hidden bg-gray-100">
      {/* Mobile menu */}
      <div className={`fixed inset-0 flex z-40 md:hidden ${sidebarOpen ? '' : 'hidden'}`}>
        <div className="fixed inset-0 bg-gray-600 bg-opacity-75" onClick={() => setSidebarOpen(false)} />
        <div className="relative flex-1 flex flex-col max-w-xs w-full bg-white">
          <div className="absolute top-0 right-0 -mr-12 pt-2">
            <button
              className="ml-1 flex items-center justify-center h-12 w-12 rounded-full focus:outline-none focus:ring-2 focus:ring-inset focus:ring-white hover:bg-gray-600 hover:bg-opacity-50 transition-colors duration-200 touch-manipulation"
              onClick={() => setSidebarOpen(false)}
              aria-label="Close navigation menu"
            >
              <X className="h-6 w-6 text-white" />
            </button>
          </div>
          <div className="flex-1 h-0 pt-5 pb-4 overflow-y-auto">
            <div className="flex-shrink-0 flex items-center px-4">
              <HarmonyLogo size={40} />
              <span className="ml-3 text-xl font-bold text-gray-900">Harmony Learning</span>
            </div>
            <nav className="mt-5 px-2 space-y-1">
              {navigation.map((item) => (
                <NavItem key={item.name} item={item} onClick={() => setSidebarOpen(false)} />
              ))}
            </nav>
          </div>
          <div className="flex-shrink-0 flex border-t border-gray-200 p-4">
            <div className="flex items-center">
              <div className="h-10 w-10 bg-gray-300 rounded-full flex items-center justify-center">
                <User className="h-6 w-6 text-gray-600" />
              </div>
              <div className="ml-3">
                <p className="text-base font-medium text-gray-700">
                  {user?.first_name} {user?.last_name}
                </p>
                <p className="text-sm font-medium text-gray-500">
                  {user?.role === 'student' ? user?.student_number : user?.email}
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Static sidebar for desktop */}
      <div className="hidden md:flex md:flex-shrink-0">
        <div className="flex flex-col w-64">
          <div className="flex flex-col h-0 flex-1 border-r border-gray-200 bg-white">
            <div className="flex-1 flex flex-col pt-5 pb-4 overflow-y-auto">
              <div className="flex items-center flex-shrink-0 px-4">
                <HarmonyLogo size={40} />
                <span className="ml-3 text-xl font-bold text-gray-900">Harmony Learning</span>
              </div>
              <nav className="mt-5 flex-1 px-2 space-y-1">
                {navigation.map((item) => (
                  <NavItem key={item.name} item={item} />
                ))}
              </nav>
            </div>
            <div className="flex-shrink-0 flex border-t border-gray-200 p-4">
              <div className="flex items-center w-full">
                <div className="h-10 w-10 bg-gray-300 rounded-full flex items-center justify-center">
                  <User className="h-6 w-6 text-gray-600" />
                </div>
                <div className="ml-3 flex-1">
                  <p className="text-sm font-medium text-gray-700">
                    {user?.first_name} {user?.last_name}
                  </p>
                  <p className="text-xs text-gray-500">
                    {user?.role === 'student' ? user?.student_number : user?.email}
                  </p>
                </div>
                <button
                  onClick={handleLogout}
                  className="ml-2 p-1 text-gray-400 hover:text-gray-600"
                  title="Logout"
                >
                  <LogOut className="h-5 w-5" />
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Main content */}
      <div className="flex flex-col w-0 flex-1 overflow-hidden">
        <div className="md:hidden pl-2 pt-2 sm:pl-4 sm:pt-4">
          <button
            className="h-12 w-12 inline-flex items-center justify-center rounded-lg text-gray-500 hover:text-gray-900 hover:bg-gray-100 focus:outline-none focus:ring-2 focus:ring-inset focus:ring-pink-500 transition-colors duration-200 touch-manipulation"
            onClick={() => setSidebarOpen(true)}
            aria-label="Open navigation menu"
          >
            <Menu className="h-6 w-6" />
          </button>
        </div>
        <main className="flex-1 relative overflow-y-auto focus:outline-none">
          <div className="py-4 px-2 sm:py-6 sm:px-4">
            <div className="max-w-7xl mx-auto">
              <Outlet />
            </div>
          </div>
        </main>
        
        {/* Footer */}
        <div className="bg-blue-600 px-4 py-6">
          <div className="flex items-center justify-center">
            <div className="flex items-center space-x-3">
              {/* AutoM8 Logo */}
              <div className="h-10 w-10 border-2 border-white rounded-lg flex items-center justify-center">
                <div className="flex flex-col items-center">
                  <div className="h-1 w-1 bg-white rounded-full mb-0.5"></div>
                  <div className="flex space-x-0.5">
                    <div className="h-1 w-1 bg-white rounded-full"></div>
                    <div className="h-1 w-1 bg-white rounded-full"></div>
                    <div className="h-1 w-1 bg-white rounded-full"></div>
                  </div>
                  <div className="h-px w-3 bg-white mt-0.5"></div>
                </div>
              </div>
              <div className="text-white">
                <div className="font-bold text-xl">AutoM8</div>
                <div className="text-sm opacity-90">Streamlining Innovation</div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Layout;
