import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useForm } from 'react-hook-form';
import { Eye, EyeOff, GraduationCap, Users } from 'lucide-react';
import { useAuth } from '../../contexts/AuthContext';
import LoadingSpinner from '../common/LoadingSpinner';
import HarmonyLogo from '../common/HarmonyLogo';
import { ThemeToggle, useTheme, ThemedLayout } from '../common/ThemeProvider';
import { SchoolHeader, AchievementBadge } from '../common/BrandingElements';

const Login = () => {
  const [userType, setUserType] = useState('student');
  const [showPassword, setShowPassword] = useState(false);
  const [loading, setLoading] = useState(false);
  const { login } = useAuth();
  const { theme } = useTheme();
  const navigate = useNavigate();

  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm();

  const onSubmit = async (data) => {
    setLoading(true);
    try {
      const result = await login(data, userType);
      if (result.success) {
        navigate('/dashboard');
      }
    } catch (error) {
      console.error('Login error:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-pink-50 to-blue-50 py-4 px-4 sm:py-12 sm:px-6 lg:px-8">
      <div className="max-w-md w-full space-y-6 sm:space-y-8">
        <div>
          <div className="mx-auto h-16 w-16 sm:h-20 sm:w-20 bg-white rounded-xl shadow-lg flex items-center justify-center p-2">
            <HarmonyLogo size={50} className="sm:hidden" />
            <HarmonyLogo size={60} className="hidden sm:block" />
          </div>
          <h2 className="mt-4 sm:mt-6 text-center text-2xl sm:text-3xl font-extrabold text-gray-900">
            Harmony Learning Institute
          </h2>
          <p className="mt-2 text-center text-sm text-gray-600">
            Sign in to your account
          </p>
        </div>
        
        <form className="mt-6 sm:mt-8 space-y-4 sm:space-y-6 bg-white p-6 sm:p-8 rounded-xl shadow-lg" onSubmit={handleSubmit(onSubmit)}>
          {/* User Type Selection */}
          <div className="flex rounded-md shadow-sm" role="group">
            <button
              type="button"
              onClick={() => setUserType('student')}
              className={`px-3 sm:px-4 py-3 sm:py-2 text-sm font-medium rounded-l-lg border flex items-center space-x-2 flex-1 justify-center transition-colors duration-200 min-h-12 touch-manipulation ${
                userType === 'student'
                  ? 'bg-pink-600 text-white border-pink-600'
                  : 'bg-white text-gray-700 border-gray-300 hover:bg-gray-50'
              }`}
            >
              <GraduationCap className="h-4 w-4" />
              <span>Student</span>
            </button>
            <button
              type="button"
              onClick={() => setUserType('staff')}
              className={`px-3 sm:px-4 py-3 sm:py-2 text-sm font-medium rounded-r-lg border flex items-center space-x-2 flex-1 justify-center transition-colors duration-200 min-h-12 touch-manipulation ${
                userType === 'staff'
                  ? 'bg-blue-600 text-white border-blue-600'
                  : 'bg-white text-gray-700 border-gray-300 hover:bg-gray-50'
              }`}
            >
              <Users className="h-4 w-4" />
              <span>Staff</span>
            </button>
          </div>

          <div className="space-y-4">
            {/* Username/Email Field */}
            <div>
              <label htmlFor="username" className="block text-sm font-medium text-gray-700">
                {userType === 'student' ? 'Student Number' : 'Email Address'}
              </label>
              <input
                id="username"
                name="username"
                type={userType === 'student' ? 'text' : 'email'}
                autoComplete={userType === 'student' ? 'username' : 'email'}
                required
                {...register(userType === 'student' ? 'student_number' : 'email', {
                  required: `${userType === 'student' ? 'Student number' : 'Email'} is required`,
                })}
                className={`mt-1 appearance-none relative block w-full px-4 py-3 border ${
                  errors[userType === 'student' ? 'student_number' : 'email'] ? 'border-red-300' : 'border-gray-300'
                } placeholder-gray-500 text-gray-900 rounded-lg focus:outline-none focus:ring-2 focus:ring-pink-500 focus:border-pink-500 text-base min-h-12`}
                placeholder={userType === 'student' ? 'Enter your student number' : 'Enter your email address'}
              />
              {errors[userType === 'student' ? 'student_number' : 'email'] && (
                <p className="mt-1 text-sm text-red-600">
                  {errors[userType === 'student' ? 'student_number' : 'email']?.message}
                </p>
              )}
            </div>

            {/* Password Field */}
            <div>
              <label htmlFor="password" className="block text-sm font-medium text-gray-700">
                Password
              </label>
              <div className="mt-1 relative">
                <input
                  id="password"
                  name="password"
                  type={showPassword ? 'text' : 'password'}
                  autoComplete="current-password"
                  required
                  {...register('password', {
                    required: 'Password is required',
                    minLength: {
                      value: 6,
                      message: 'Password must be at least 6 characters',
                    },
                  })}
                  className={`appearance-none relative block w-full px-4 py-3 pr-12 border ${
                    errors.password ? 'border-red-300' : 'border-gray-300'
                  } placeholder-gray-500 text-gray-900 rounded-lg focus:outline-none focus:ring-2 focus:ring-pink-500 focus:border-pink-500 text-base min-h-12`}
                  placeholder="Enter your password"
                />
                <button
                  type="button"
                  className="absolute inset-y-0 right-0 pr-3 flex items-center touch-manipulation min-h-12 min-w-12"
                  onClick={() => setShowPassword(!showPassword)}
                  aria-label={showPassword ? 'Hide password' : 'Show password'}
                >
                  {showPassword ? (
                    <EyeOff className="h-5 w-5 text-gray-400" />
                  ) : (
                    <Eye className="h-5 w-5 text-gray-400" />
                  )}
                </button>
              </div>
              {errors.password && (
                <p className="mt-1 text-sm text-red-600">{errors.password.message}</p>
              )}
            </div>
          </div>

          <div>
            <button
              type="submit"
              disabled={loading}
              className={`group relative w-full flex justify-center py-3 px-4 border border-transparent text-base font-medium rounded-lg text-white transition-colors duration-200 min-h-12 touch-manipulation ${
                userType === 'student' 
                  ? 'bg-pink-600 hover:bg-pink-700 focus:ring-pink-500' 
                  : 'bg-blue-600 hover:bg-blue-700 focus:ring-blue-500'
              } focus:outline-none focus:ring-2 focus:ring-offset-2 disabled:opacity-50 disabled:cursor-not-allowed`}
            >
              {loading ? (
                <LoadingSpinner size="sm" className="mr-2" />
              ) : null}
              {loading ? 'Signing in...' : 'Sign in'}
            </button>
          </div>

          {/* Demo credentials info */}
          <div className="bg-gray-50 rounded-lg p-4 mt-6">
            <h3 className="text-sm font-medium text-gray-900 mb-2">Demo Credentials</h3>
            <div className="text-xs text-gray-600 space-y-1">
              <p><strong>Super Admin:</strong> admin@harmonylearning.edu / admin123</p>
              <p><strong>Student:</strong> Use your student number for both username and password</p>
            </div>
          </div>
        </form>

        {/* Footer */}
        <div className="text-center">
          <div className="bg-white rounded-lg p-4 shadow-sm">
            <p className="text-xs text-gray-500 mb-2">Powered by</p>
            <div className="flex items-center justify-center space-x-2">
              <div className="h-8 w-8 bg-gradient-to-r from-purple-500 to-pink-500 rounded-full flex items-center justify-center">
                <span className="text-white font-bold text-sm">A8</span>
              </div>
              <span className="text-sm font-medium text-gray-700">AutoM8</span>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
